export declare const CodeEditorStyle = "\n  .rek-code-block {\n    background-color:var(--input-background-color);\n    padding:8px;     \n    white-space:pre-wrap;\n    line-height:1.3;\n    tab-size: 4;\n  }  \n\n .rek-code-block.sql,\n .rek-code-block.perl,\n .rek-code-block.css,\n .rek-code-block.go,\n .rek-code-block.clojure,\n .rek-code-block.markup,\n .rek-code-block.swift,\n .rek-code-block.clike,\n .rek-code-block.js {   \n  \n  .rek-code-keyword {\n    color:blue;\n  }\n  \n  .rek-code-keyword {\n    color:blue;\n  }\n  \n  .rek-code-function {\n    color:blue;\n  }\n  \n  .rek-code-punctuation {\n    color:orange;\n  }\n\n  .rek-code-variable {\n    color:blue;\n  }\n  \n  .rek-code-number {\n    color:mediumseagreen;\n  }\n  \n  .rek-code-string {\n    color:mediumseagreen;\n  }\n\n  .rek-code-boolean {\n    color:mediumseagreen;\n  }\n\n  .rek-code-attr-value {\n    color:mediumseagreen;\n  }\n\n  .rek-code-tag {\n    color:blue;\n  }\n }\n";
